import { Component, OnInit } from '@angular/core';
import { JsonService } from '@services/json.service';
import { FuzzySearchService } from '@services/fuzzy-search.service';
import { Item } from '@usertypes/types';

@Component({
    selector: 'app-root',
    templateUrl: './app.component.html',
    styleUrls: ['./app.component.sass']
})
export class AppComponent implements OnInit {
    title = 'App';
    searchList: Item[] = [];
    searchStack: Item[][] = [];

    constructor(private jsonService: JsonService,
        private fuzzySearchService: FuzzySearchService) {
    }

    ngOnInit(): void {
        this.jsonService.fetchSearchList().subscribe(data => {
            this.searchList = data;
            this.searchStack.push(data);
            console.log(this.searchStack);
        });
    }

    public fuzzySearch(event: Event): void {
        // Event guaranteed to be an InputEvent
        const inputEvent = event as InputEvent;
        const inputWasBackspace = inputEvent.inputType == "deleteContentBackward"

        if (inputWasBackspace && this.searchStack.length != 0) {
            // Restore previous search list
            this.searchList = this.searchStack.pop() as Item[];
        } else {
            // Guaranteed to be non-null since the user must have input something
            // for this handler to be triggered
            const searchTerm: string = inputEvent.data as string;
            this.searchList = this.fuzzySearchService.search(this.searchList, searchTerm);
        }
        // If the results of the search did not change, don't overwrite
        // the last search list
        // Prevent erasing the last 'useful search' where results were different
        const peek = this.searchStack.slice(-1).pop();
        console.log(peek);
        if (peek != this.searchList) {
            this.searchStack.push(peek);
            this.searchStack.push(this.searchList);
