import { Injectable } from '@angular/core';
import Fuse from 'fuse.js';
import { Item } from '@usertypes/types';

@Injectable({
  providedIn: 'root'
})
export class FuzzySearchService {

  constructor() { }

  public search(data: Item[], searchTerm: string): Item[] {
    const options = {
      keys: ['id', 'name', 'value', 'listorder'], // Adjust according to the properties you want to search
      threshold: 0.3 // Adjust this value to change sensitivity, 0 is exact match, 1 matches anything.
    };

    const fuse = new Fuse(data, options);
    return fuse.search(searchTerm).map(result => result.item);
  }
}
